import React from 'react';
import Signup from './Signup';
import Login from './Login';
import './Signin.css';
import logo from './logo.svg';

export default props =>

    <div className='App'>
        <div className='App-header'>
            <h1>Color Palette Inspector</h1>
                {/* <label htmlFor='single'>
                <img src={logo} className="App-logo" alt="logo" />
                </label> */}
            {/* <input type='file' id='single' onChange={props.onChange} /> */}
            <div>
                <div id="buttons">
                    <p id="signupButton" onClick={props.switch.bind(null,"signup")} className={props.signup ? "yellow":"blue"}>Sign In</p>
                    <p id="loginButton" onClick={props.switch.bind(null,"login")} className={props.login ? "yellow":"blue"}> Login</p>
                </div>
                {props.signup?<Signup loginInfo={props.loginInfo} onChange={props.onChangeInfo, props.onChange}/> : null}
                {props.login? <Login loginInfo={props.loginInfo} onChange={props.onChangeInfo, props.onChange}/> : null}
            </div>
        </div>
    </div>




